import React from 'react';
import logo from './logo.png';
import './App.css';
import './fonts.css';

import * as Components from './Components';
import { firebase } from './firebase';
import { createUserWithEmailAndPassword, updateProfile } from "firebase/auth";
import { auth, db, storage } from "../firebase";
import { ref, uploadBytesResumable, getDownloadURL } from "firebase/storage";
import { doc, setDoc } from "firebase/firestore";
import { useNavigate, Link } from "react-router-dom";




function App() {
  const [signIn, toggle] = React.useState(true);

  return (
    <div className="App">

      <header className="App-header">


         <nav > 

         <img src={logo} className="App-logo" alt="logo" />
          <ul >
              <li><button-header onClick={() => toggle(true)} >Sign In</button-header></li>   
              <li><button-header-1 onClick={() => toggle(false)} >Sign Up </button-header-1> </li>

            </ul>
         </nav>

      </header>

      <div className="body"> 

      <Components.Container>
              <Components.SignUpContainer signinIn={signIn}>
                  <Components.Form>
                      <Components.Title>Create Account</Components.Title>
                      <Components.Input type='text' placeholder='Name' id="name-register" />
                      <Components.Input type='email' placeholder='Email' id="email-register"/>
                      <Components.Input type='password' placeholder='Password' id="password-register"/>
                      <Components.Button>Sign Up</Components.Button>
                  </Components.Form>
              </Components.SignUpContainer>

              <Components.SignInContainer signinIn={signIn}>
                   <Components.Form>
                       <Components.Title>Sign in</Components.Title>
                       <Components.Input type='email' placeholder='Email' id="name-login"/>
                       <Components.Input type='password' placeholder='Password' id="password-login"/>
                       <Components.Anchor href='#'>Forgot your password?</Components.Anchor>
                       <Components.Button>Sign In </Components.Button>
                   </Components.Form>
              </Components.SignInContainer>

              <Components.OverlayContainer signinIn={signIn}>
                  <Components.Overlay signinIn={signIn}>

                  <Components.LeftOverlayPanel signinIn={signIn}>

                  </Components.LeftOverlayPanel>

                      <Components.RightOverlayPanel signinIn={signIn}>
                        <Components.Title></Components.Title> 
                      </Components.RightOverlayPanel>

                  </Components.Overlay>
              </Components.OverlayContainer>

          </Components.Container>

      </div>
      <div
        className="Copyrights">
      </div>

    </div>


  );
}

// Action listener used when button is pressed
// button.addEventListener( "click", register );

export default App;
