import {auth, storage, db} from "../Model/firebase"; 

const SearchResults =  (props) => {

    const [accounts, setAccounts] = useState([]);

    console.log(props.searchText)
    const [stocks, setQuantity] =  useState([

        { name: "Apple",
          id: "apple",
          symbol: "aapl",
