import React, { useState } from "react";
import "../Style/ForgotPassword.css";
import { Link } from "react-router-dom";
import {auth} from "../Database/firebase"; 




const ForgotPassword = () => {
        const style = {
            fontWeight: "bold",
            color: "white"
        }

        const verifyEmail = () => {
            sendEmailVerification(auth.currentUser)
            .then( () => {
              alert("Email verification sent!");
            })
            .catch( (error) => {
              // When this happen we should put an extra button to send the verification again
              alert("Could not send verification email.");
            });
          };


    return(

        <div className="ForgotPassword"> 
                <div className="Container"> 
                    <div className="Link">
                    <h6>Please enter your email address. You will receive a link to create a new password via email.</h6>
                    <input type="text" placeholder="Email" id="email"></input> 
                    <button onClick={verifyEmail}>Send link</button>
                    <Link style={style}to="/"> Back to Login</Link>
                    </div>
                </div>

        </div>
    );
}


export default ForgotPassword;
