import React, { useState } from "react";
// import {useEffect } from "react";
import logo from './logo.png';
import profilepicture from './profilepicture.png';
import plus from './plus.png';
// import UploadImage from "./UploadImage";
import "./LoginRegister.css";
// import authUser from './AuthDetails';
import { InputMask } from 'primereact/inputmask';
import { Password } from 'primereact/password';
// import { Button } from 'primereact/button';
// import { Dialog } from 'primereact/dialog';
import { InputText } from 'primereact/inputtext';



// exported out of firebase.js because getAuth is called there
// if it was called here, it was running before InitializeApp method

import {auth} from "./firebase"; 
import { signInWithEmailAndPassword, createUserWithEmailAndPassword } from "firebase/auth";

const LoginRegister = () => {

  // hooks for state variables, they can be changed using the method next to it
  const [addclass, setaddclass] = useState("");
  const [email,setEmail] = useState("");
  const [password,setPassword] = useState("");
  const [value,setValue] = useState(null);
  const [visible, setVisible] = useState(false);
  const [phonenumber, setPhoneFormat] = useState("");
