import React, { useState } from 'react';
import DataTable from 'react-data-table-component';

const Table = ({controller}) => {
  const [data, setData] = useState([]);

  const handleAddRow = (newData) => {
    setData([...data, newData]);
  };

  const columns = [
    {
      name: 'Stock',
      selector: 'stock',
      sortable: true,
    },
    {
      name: 'Amount',
      selector: 'amount',
      sortable: true,
    },
    {
      name: 'Time',
      selector: 'time',
      sortable: true,
    },
  ];

  return (
    <>
      <DataTable
        columns={columns}
        data={data}
        pagination
      />
      <div>
        <h3>Add New Row</h3>
        <form onSubmit={(e) => {
          e.preventDefault();
          const { stock, amount, time } = e.target.elements;
          handleAddRow({ stock: stock.value, amount: amount.value, time: time.value });
          e.target.reset();
        }}>
          <label htmlFor="stock">Stock:</label>
          <input id="stock" type="text" name="stock" />

          <label htmlFor="amount">Amount:</label>
          <input id="amount" type="text" name="amount" />

          <label htmlFor="time">Time:</label>
          <input id="time" type="text" name="time" />

          <button type="submit">Add Row</button>
        </form>
      </div>
    </>
  );
};

export default Table;
