import React, { useState } from "react";
import logo from '../Icons/logo.png';
import profilepicture from '../Icons/profilepicture.png';
import plus from '../Icons/plus.png';
import erroricon from '../Icons/error.png';
import upload from '../Icons/upload.png';
import "../Style/LoginRegister.css";
// import authUser from './AuthDetails';
import { InputMask } from 'primereact/inputmask';
import { Password } from 'primereact/password';
// import ForgotPassword from '../Pages/ForgotPassword';
// import { Button } from 'primereact/button';
// import { Dialog } from 'primereact/dialog';
import { InputText } from 'primereact/inputtext';
import {auth, storage} from "../Database/firebase"; 
import {verifyEmail} from "../Database/firebase"; 
import { Link } from "react-router-dom";
import { setDoc, doc } from 'firebase/firestore';
import db from '../Database/firebase';
import AuthDetails from '../Database/AuthDetails';
import { ref, uploadBytes } from 'firebase/storage';



// exported out of firebase.js because getAuth is called there
// if it was called here, it was running before InitializeApp method

import { signInWithEmailAndPassword, createUserWithEmailAndPassword, sendEmailVerification, isEmailVerified } from "firebase/auth";

const LoginRegister = () => {
  
  // hook to activate the overlay effect
  const [addclass, setaddclass] = useState("");

  // Hooks for email verification section
  const [email,setEmail] = useState("");
  const [password,setPassword] = useState("");

  // Hooks with user registry information
  const [prefix, setPrefix] = useState("");
  const [name, setName] = useState("");
  const [midName, setMidName] = useState("");
  const [suffix, setSuffix] = useState("");
  const [lastName, setLastName] = useState("");
  const [secondLastName, setSecondLastName] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [countryCode, setCountryCode] = useState("");
  const [country, setCountry] = useState("");
  const [zipCode, setZipCode] = useState("");
  const [phonenumber, setPhone] = useState("");
  const [address1, setAddress1] = useState("");
  const [address2, setAddress2] = useState("");
  const [identificationType, setIDFormat] = useState("");
  const [idvalue,setIDValue] = useState("");
  const [image, setImage] = useState("");   // Profile picture
  const [idFile, setIdFile] = useState(""); // ID picture
  const [ImgIsUploaded, setImgIsUploaded] = useState(false);
  const [IdIsUploaded, setIdIsUploaded] = useState(false);
  const [typeFile,setTypeFile] = useState("");
  const [userAdded, setUser] = useState(false);

  /* ----------------------Front-end functions------------------*/
  const cancel = () => { 

    setPrefix(""); setName(""); setEmail(""); setMidName(""); setSuffix("");
    setLastName(""); setSecondLastName(""); setPassword(""); setConfirmPassword("");
    setCountryCode(""); setCountry(""); setZipCode(""); setPhone("");
    setAddress1(""); setAddress2(""); setIDFormat(""); setIDValue("");
    setImage(""); setImgIsUploaded(false); setIdIsUploaded(false); setIdFile("")
  }

  const clearLogin = () =>{
    cancel();
    setaddclass("");
  }

  const clearSignUp= () =>{
    cancel();
    setaddclass("right-panel-active")
  }

  const phoneMask = (e) => {
    let result;
    if(countryCode === "506"){
      result = "9999-9999"
    }else if(countryCode ==="1"){
      result = "999-999-9999"
    }else{
      result = " "
    }
    return result;
  };

  const idMask = (e) => {
    let result;
    if(identificationType === "id"){
      result = "99-9999-9999"
    }else if(identificationType ==="ssn"){
      result = "999-99-9999"
    }else if (identificationType === "dimex"){
      result = "999999999999"
    }
    else if (identificationType === "passport"){
      result = "****************************************************************************************************"
    }
    else if (identificationType === "other"){
      result = "99999999999999999999999"
    }
    else{
      result = " "
    }
    return result;
  };

  function handleImageChange(e) {
    if (e.target.files && e.target.files[0]) {
      setTypeFile(e.target.files[0].type);
      let reader = new FileReader();

      reader.onload = function (e) {
        setImage(e.target.result); // changes the profile picture variable through its hook
        setImgIsUploaded(true); // status set to true
      };

      reader.readAsDataURL(e.target.files[0]);
    }
  }


  function handleIDChange(e) {
    if (e.target.files && e.target.files[0]) {
      let reader = new FileReader();

      reader.onload = function (e) {
        setIdFile(e.target.result);
        setIdIsUploaded(true);
      };

      reader.readAsDataURL(e.target.files[0]);
    }
  }

  /*-----------------------Back-end functions---------------------------*/

  // sends verification email to the user
  const verifyEmail = () => {
    sendEmailVerification(auth.currentUser)
    .then( () => {
      alert("Email verification sent!");
    })
    .catch( (error) => {
      // When this happen we should put an extra button to send the verification again!!!!
      alert("Could not send verification email");
    });
  };

  // Checks if the 2 password fields are equal & have security minimum requirements 
  const passValidation = (p1, p2) => {
    
    if (p1 !== p2){ // Check the 2 password boxes
      alert("Passwords did not match");
      return false;
    }

    let regx = /^(?=.*[!@#?])(?=.*[a-z])(?=.*[A-Z])(?=.*[0-9])(?!.*\s).{8,16}$/;
    
    if ( p1.match(regx) ){ // contains all required characters
      return true;
    }
    else{ // Does not contain all characters
      alert("Password does not meet the requirements.");
      return false;
    }
  }

  // logs into authentication service
  const logIn = (e) => {
    
    e.preventDefault(); // when page is reloaded, state of email and password are not reset back to default
    
    signInWithEmailAndPassword(auth, email, password)
    .then( (userCredential)=>{
        console.log(userCredential) // userCredential is what firebase returns if succesful login
    }).catch(error => alert(error.message))
  };

  // signs up user into the authentication service with the email and password chosen
  const signUp = (e) => {
    e.preventDefault();
    
    if ( passValidation(password, confirmPassword) ){ // Only create user if the password passed validations

      createUserWithEmailAndPassword(auth, email, password)
      .then( (userCredential)=>{
          alert("User created.");
          setUser(true);
          verifyEmail();
      }).catch( error => alert(error.message));
    }
  };
  
  // puts profile specified picture into storage
  const uploadImage = ( namePicture, handletoPicture ) => { 
    // Grab a reference to the location where we want to upload the image
    const imageRef = ref( storage, 'users/'+ auth.currentUser.uid +'/pictures/'+ namePicture+'.jpg');
    // Function uploadBytes returns a promise
    uploadBytes( imageRef, handletoPicture ).then( ( ) => {
        console.log( "image uploaded" );    
    } ).catch( ( ) => { 
        alert( "Error uploading image!" );
    } );
  };

  const userInfo = [ ];
  userInfo.push( prefix, name, midName,lastName,secondLastName, suffix, countryCode, phonenumber,country , zipCode, address1, address2, identificationType, idvalue );


// Database user creation: includes firestore data as well as storage of pictures
const addMember = () => {
  /* Use of async function to use await keyword ( acts as a promise ) */
  const Submit = async ( ) => {
    // Grab a reference to the "users collection", with new field called "unknown"
    const docReference = doc( db, "users", auth.currentUser.uid )
    
    // Add new data
    const payload = { prefix: userInfo[0],
                      name: userInfo[ 1],
                      midName: userInfo[ 2 ],
                      lastName: userInfo[ 3 ],
                      secondLastName: userInfo[ 4 ],
                      suffix: userInfo[ 5 ],
                      countryCode: userInfo[ 6 ],
                      phonenumber: userInfo[7],
                      country: userInfo[8],
                      zipCode: userInfo[ 9 ],
                      address1: userInfo[ 10 ],
                      address2: userInfo[ 11 ],
                      idType: userInfo[ 12],
                      id: userInfo[ 13 ],
                    }; 
    // Set new data into database
    await setDoc( docReference, payload );
  }

  alert("Adding member to database.");
  Submit();
  
  // Upload user's pictures to storage
  uploadImage("profilePicture",image);
  uploadImage("IDpicture", idFile);

  cancel();
}


  return (

    <div className="App"> 
      {/* <header className="App-header">
        <nav>
          <img src={logo} className="App-logo" alt="logo" />
          <ul>
            <li><button-header  onClick={() => setaddclass("")}>Sign In</button-header></li>
            <li><button-header onClick={() => setaddclass("right-panel-active")}>Sign Up </button-header> </li>
          </ul>
        </nav>
        </header> */}
    <div className="body">

    <div className={`container ${addclass}`} id="container">
      <div className="form-container  sign-up-container">
        <form onSubmit={signUp} id="signup-form">
          <h1>Create Account</h1>


          {!userAdded ?  (
              <>
                    <div className="Email-Box">
              <span style={{ color: 'red', fontWeight: 'bold' }}>*</span> 
              <input type="email" placeholder="Email" required value={email} onChange={(e)=>setEmail(e.target.value)} name="email"/>

          </div>
         
          <div className="Password-Box">
              <span style={{ color: 'red', fontWeight: 'bold' }}>*</span> 
              <Password placeholder="Password"  required value={password} onChange={(e)=>setPassword(e.target.value)} name="password" toggleMask />
           </div>
          
           <div className="PasswordConfirm-Box">
              <span style={{ color: 'red', fontWeight: 'bold' }}>*</span> 
              <Password placeholder="Confirm Password"  required   value={confirmPassword} onChange={(e)=>setConfirmPassword(e.target.value)}  name="confirmpassword" toggleMask />
           </div>

           <ul>
            <uli>
                <button type="submit" >Verify Email</button>
              <button type="button" onClick={cancel}>CANCEL</button>
            </uli>
          </ul>
          


              </>
            )
            :

            ( 
            <>
            <div className="UploadImage">
              <div className="profile-pic">

                { ImgIsUploaded ? (
                  <>
                    <label htmlFor="inputImg"> 
                      <img id="picture" src={image} draggable={false} alt="uploaded-img" /> 
                    </label>
                    <div className="CameraIcon"> 
                      <img src={erroricon} id="close" alt="plus" onClick={() => {setImgIsUploaded(false); setImage(null)}} ></img> 
                    </div>
                  </>) 
                  : 
                  (
                  <>
                      <label htmlFor="inputImg"> <img src={profilepicture} id="picture" alt="profilepicture" ></img> </label> 
                      <div className="CameraIcon"> <label htmlFor="inputImg"> <img src={plus} id="plus" alt="plus" ></img> </label>     </div>  
                      <input type="file"  accept="image/jpg" id="inputImg" onChange={handleImageChange}/>
                  </>
                  ) }
               
            </div>
          </div>

          <div className=" NameHolder">
             
          <div id="Prefix-Box"> 
              <select id="prefix" name="Prefix" class="form-control" value={prefix} onChange={(e)=>setPrefix(e.target.value)} >
                    <option>Prefix</option>
                    <option value="Mr." Selected>Mr.</option>
                    <option value="Mrs." Selected>Mrs.</option>
                    <option value="Ms" Selected>Ms</option>
                    <option value="Miss" Selected>Miss</option>
                    <option value="Mx" Selected>Mx</option>
                    <option value="Sir" Selected>Sir</option>
                    <option value="Dr." Selected>Dr.</option>
                    <option value="Dame" Selected>Dame</option>
                    <option value="Lord" Selected>Lord</option>
                    <option value="Lady" Selected>Lady</option>
                    <option value="Cllr" Selected>Cllr</option>
                    <option value="President" Selected>President</option>
                    <option value="Father" Selected>Father</option>
                    <option value="Gentlemen" Selected>Gentlemen</option>
                    <option value="Madam" Selected>Madam</option>
                    <option value="Professor" Selected>Professor</option>
                    <option value="Er." Selected>Er.</option>
                </select>

             </div> 

             <div id="Name-Box"> 
                <span style={{ color: 'red', fontWeight: 'bold' }}>*</span>
                <input placeholder="Name" required value={name} onChange={(e)=>setName(e.target.value)} />

             </div>
             
             <div id="MiddleName-Box"> 
             <input type="text" placeholder="Middle Name" value={midName} onChange={(e)=>setMidName(e.target.value)}/>
             </div>
              
          </div>
              
              <div className="LastName">
                    <div id="LastName-Box">
                        <span style={{ color: 'red', fontWeight: 'bold' }}>*</span>
                        <input type="text" placeholder="Last Name" required value={lastName} onChange={(e)=>setLastName(e.target.value)}/>
                    </div>
                   
                    <input type="text" placeholder="Second Last Name" value={secondLastName} onChange={(e)=>setSecondLastName(e.target.value)}/>

                    <div id="Suffix-Box"> 
                      <select id="suffix" name="Suffix" class="form-control" value={suffix} onChange={(e)=>setSuffix(e.target.value)} >
                          <option>Suffix</option>
                          <option value="Sr." Selected>Sr.</option>
                          <option value="Jr." Selected>Jr.</option>
                          <option value="PhD" Selected>PhD</option>
                          <option value="MSW" Selected>MSW</option>
                          <option value="II" Selected>II</option>
                          <option value="III" Selected>III</option>
                          <option value="IV" Selected>IV</option>
                      </select>

                    </div> 
              </div>

              
         <div className="PhoneNumber">
                
                <div id="PhoneLabel-Box"> 
                <span style={{ color: 'red', fontWeight: 'bold'}}>*</span> 
                    <label-1> Phone Number</label-1>
                </div>
                
              
                <select id="CountryCode" name="CountryCode" class="form-control" required value={countryCode} onChange={(e) => setCountryCode(e.target.value)}>
                      <option value="0"> Country Code</option>
                            <option data-countryCode="CR" value="506" Selected>CR (+506)</option>
                            <option data-countryCode="US" value="1" Selected>US (+1)</option>
                </select> 

                <div id="PhoneNumber-Box"> 
                      <InputMask value={phonenumber} required onChange={(e) => setPhone(e.target.value)} mask={phoneMask()} placeholder={phoneMask()}> </InputMask>
                </div>

               
         </div>

          <div className="Country">
              <label for="country" id="CountrySelector"></label>      
                <select id="country" name="country" class="form-control" required value={country} onChange={(e) => setCountry(e.target.value)} >
                    <option>Country</option>
                    <option value="Costa Rica" Selected>Costa Rica</option>
                    <option value="United States" Selected>United  States</option>
                </select>

              <input type="text" placeholder="Zip Code" value={zipCode}  onChange={(e) => setZipCode(e.target.value)} name="ZipCode" />
          </div>

          <input type="text" placeholder="Address 1*" required value={address1}  onChange={(e) => setAddress1(e.target.value)} name="Address1"/>
          <input type="text" placeholder="Address 2" value={address2} onChange={(e) => setAddress2(e.target.value)} name="Address2"/>

          {/* Buttons: Register & Cancel */}

          <div className="ID">
                <label for="identification" id="IDSelector"></label>      
                  <select id="id" name="id" class="id-control" required value={identificationType} onChange={(e) => setIDFormat(e.target.value)}>
                      <option>Identification</option>
                      <option value="id" Selected>ID</option>
                      <option value="ssn" Selected>Social Security Number</option>
                      <option value="dimex" Selected>DIMEX</option>
                      <option value="passport" Selected>Passport</option>
                      <option value="other" Selected>Other</option>
                  </select>
                <InputMask value={idvalue} onChange={(e) => setIDValue(e.target.value)} mask={idMask()} placeholder={idMask()}> </InputMask>

                <label htmlFor="inputID" style={{fontSize: '12px', paddingRight: '6px', color: 'rgb(110, 110, 110)'}}> Choose a file </label> 
                <div className="IDIcon"> <label htmlFor="inputID"> <img src={upload} id="upload" alt="upload" ></img> </label> </div>  
                <input type="file"  accept="image/jpg" id="inputID" onChange={handleIDChange}/>
                
          </div>

      

          <ul>
            <uli>
                <button type="button" onClick={() => { addMember(); }} >REGISTER</button>
              <button type="button" onClick={cancel}>CANCEL</button>
            </uli>
          </ul>
          

                    
            </>
            
            )
          }
          
           
                   
         
        </form>
      </div>
      <div className="form-container sign-in-container">
        <form onSubmit={logIn} id="login-from">
          <h1>Login</h1>
          <input type="email" placeholder="Email" value={email} onChange={(e)=>setEmail(e.target.value)}/>
          <Password placeholder="Password*"  required value={password} onChange={(e)=>setPassword(e.target.value)} name="password" toggleMask />
         
          <Link to="/forgot"> Forgot Password?</Link>

          <button type="submit">LOGIN</button>

          <AuthDetails></AuthDetails> 
          {/* {err && <span>Something went wrong</span>} */}
        </form>
      </div>
      <div className="overlay-container">
        <div className="overlay">
          <div className="overlay-panel overlay-left">
            <h4> Already have an account? </h4>
            <button
              className="ghost"
              id="signIn"
              onClick={() => clearLogin()}>
              LOG IN
            </button>
          </div>
          <div className="overlay-panel overlay-right">
          <h4> Not a member yet? </h4>
            <button
              className="ghost"
              id="signUp"
              onClick={() => clearSignUp()}
            >
              SIGN UP
            </button>
          </div>
        </div>
      </div>
    </div>
    </div>

    </div>

    

  );
};

export default LoginRegister;
