// will have the logic to check
// if user is signed in or not and which user it is
import { onAuthStateChanged, signOut } from 'firebase/auth';
import React, { useEffect, useState } from 'react';
import {auth} from "./firebase";

const AuthDetails = () => {
    const [authUser, setAuthUser] = useState(null);

    // Tells react that component need to run this after render
    useEffect(() => {
        // listener: if user exist and comebacks after function call
        // react depending on situation
        const listen = onAuthStateChanged(auth, (user) => {
            if (user) {
                setAuthUser(user);
            } else{
                setAuthUser(null);
            }
            });

            // return the event listener we just received
            return () => {listen()};

        }, []);

    const userSignOut = () => {
        signOut(auth)
        .then( () => { console.log("Signed out succesfully.") } )
        .catch( error => console.log(error) )
    };

    return ( 
        <AuthDetails.onAuthStateChanged value={{authUser}}>
            {Children}
        </AuthDetails.onAuthStateChanged>
        // <div> 
        //     { authUser ? <><p>{`Signed In as ${authUser.email}`}</p> 
        //     <button onClick={userSignOut}>Sign Out</button></>
        //     // else you are signed out
        //     : <p>Signed Out</p>
        //     }  
        // </div>

    );
};

export default AuthDetails;
