import React, { useState } from "react";
// import {useEffect } from "react";
import logo from './logo.png';
import profilepicture from './profilepicture.png';
import plus from './plus.png';
// import UploadImage from "./UploadImage";
import "./LoginRegister.css";
// import authUser from './AuthDetails';

// exported out of firebase.js because getAuth is called there
// if it was called here, it was running before InitializeApp method
import {auth} from "./firebase"; 
import { signInWithEmailAndPassword, createUserWithEmailAndPassword } from "firebase/auth";

const LoginRegister = () => {

  const [addclass, setaddclass] = useState("");

  // hooks for state variable, it can be changed using the method next to it
  const [email,setEmail] = useState("");
  const [password,setPassword] = useState("");
  const [message, setMessage] = useState("");


  // method that runs when form is submitted
  // e stands for event
  const logIn = (e) => {
    // when page is reloaded, state of email and password are not reset back to default
    e.preventDefault();
    //returns a promise with firebase answer to our query
    signInWithEmailAndPassword(auth, email, password)
    .then( (userCredential)=>{
        console.log(userCredential)
    }).catch(error => alert(error.message))
  };

  // method that runs when form is submitted
  // e stands for event
  const signUp = (e) => {
    // when page is reloaded, state of email and password are not reset back to default
    e.preventDefault();
    //returns a promise with firebase answer to our query
    createUserWithEmailAndPassword(auth, email, password)
    .then( (userCredential)=>{
        console.log(userCredential)
    }).catch(error => alert(error.message));
  };

  const cancel = () => { 
    document.getElementById("create-course-form").reset();
  }



  return (

    <div className="App"> 
      <header className="App-header">
        <nav>
          <img src={logo} className="App-logo" alt="logo" />
          <ul>
            <li><button-header  onClick={() => setaddclass("")}>Sign In</button-header></li>
            <li><button-header onClick={() => setaddclass("right-panel-active")}>Sign Up </button-header> </li>
          </ul>
        </nav>
        </header>
    <div className="body">
    <div className={`container ${addclass}`} id="container">
      <div className="form-container  sign-up-container">
        <form onSubmit={signUp} id="signup-form">
          <h1>Create Account</h1>

          <div className="UploadImage">
            <div className="profile-pic">
              <input type="file"  accept="image/jpg" id="inputImg" />
              <label htmlFor="inputImg"> <img src={profilepicture} id="picture" alt="profilepicture" ></img> dggg</label>
              <div className="CameraIcon"> <img src={plus} id="plus" alt="plus" ></img> </div>  
          </div>
      </div>

         {/* <UploadImage></UploadImage> */}

          <input type="text" placeholder="Name*" required/>
          <input type="text" placeholder="Last Name*" required/>
          <input type="email" placeholder="Email*" required value={email} onChange={(e)=>setEmail(e.target.value)}/>
          <input type="tel" placeholder="Phone Number*" />
          <label for="country" id="CountrySelector"></label>      

            <select id="country" name="country" class="form-control">
                <option value="CostaRica">Select Country</option>
                <option value="CostaRica">Costa Rica</option>
                <option value="US">United  States</option>
             </select>

          <input type="password" placeholder="Password*" required value={password} onChange={(e)=>setPassword(e.target.value)}/>
          <ul>
            <uli>
                <button type="submit">REGISTER</button>
              <button type="button" onClick={cancel}>CANCEL</button>
            </uli>
          </ul>

        </form>
      </div>
      <div className="form-container sign-in-container">
        <form onSubmit={logIn} id="login-from">
          <h1>Login</h1>
          <input type="email" placeholder="Email" value={email} onChange={(e)=>setEmail(e.target.value)}/>
          <input type="password" placeholder="Password" value={password} onChange={(e)=>setPassword(e.target.value)}/>
          <button type="submit">LOGIN</button>
          {/* {err && <span>Something went wrong</span>} */}
        </form>
      </div>
      <div className="overlay-container">
        <div className="overlay">
          <div className="overlay-panel overlay-left">
            <h4> Already have an account? </h4>
            <button
              className="ghost"
              id="signIn"
              onClick={() => setaddclass("")}>
              LOG IN
            </button>
          </div>
          <div className="overlay-panel overlay-right">
          <h4> Not a member yet? </h4>
            <button
              className="ghost"
              id="signUp"
              onClick={() => setaddclass("right-panel-active")}
            >
              SIGN UP
            </button>
          </div>
        </div>
      </div>
    </div>
    </div>

    </div>





  );
};

export default LoginRegister;
