          stockimg: 'intel',
          price: 29.66,
          quantity: null
        },
        { name: "Getty",
          id: 'getty',
          symbol: "gety",
          stockimg: 'getty',
          price: 6.63,
          quantity: null
        },
        { name: "Alibaba Group",
          id: 'alibaba',
