          stockimg: 'alibaba',
          price: 86.89,
          quantity: null
        },
        { name: "Bank of America",
          id: 'bac',
          symbol: "bac",
          stockimg: 'bac',
          price: 29.76,
          quantity: null
        },
        { name: "KeyCorp",
          id: 'keycorp',
          symbol: "key",
          stockimg: 'keycorp',
          price: 11.13,
          quantity: null
        },
        { name: "AMC Entertainment",
          id: 'amc',
          symbol: "amc",
          stockimg: 'amc',
          price: 4.96,
          quantity: null
        },
        { name: "Vale S.A.",
          id: 'vale',
          symbol: "vale",
          stockimg: 'vale',
          price: 14.14,
          quantity: null
        },
        { name: "NIO Inc.",
          id: 'nio',
