        },
        { name: "Ambev S.A.",
          id: 'ambev',
          symbol: "abev",
          stockimg: 'ambev',
          price: 2.82,
          quantity: null
        },
        { name: "Nokia",
          id: 'nokia',
          symbol: "nok",
          stockimg: 'nokia',
          price: 4.20,
          quantity: null
        },
        { name: "Fox Corporation",
          id: 'fox',
          symbol: "fox",
          stockimg: 'fox',
          price: 29.91,
          quantity: null
        },
        { name: "Zoom Inc.",
          id: 'zoom',
          symbol: "zm",
          stockimg: 'zoom',
          price: 65.80,
          quantity: null
      },
    ])



    // useEffect(() => {
    //     // Define an async function inside useEffect to be able to use await
    //     const fetchData = async () => {

    //     const docRef = doc(db, 'users', controller.authUser.uid);
    //     const docSnap = await getDoc(docRef);
    //     const arr = docSnap.data()["accounts"];
    //     let accountsArr = [];
    //     for (var key in arr)
    //          accountsArr.push(arr[key]);

    //     console.log( accountsArr );
    //     setAccounts(accountsArr);
    //     console.log( accounts );

    //     };
    //     fetchData();
    // }, []);


    const handleChange = (e, index) => {
        const newFiltered = [...filtered];
        newFiltered[index][e.target.name] = e.target.value;
        setQuantity(
          stocks.map((stock) => (stock.id === newFiltered[index].id ? newFiltered[index] : stock))
        );
      };



    const handleBuyStock = (account, quantity) => {
            console.log(quantity, account);

    };

    const filtered = stocks.filter((stock) =>
    stock.id.includes(props.searchText) ||
    stock.symbol.includes(props.searchText)
  );




    return(

        <div className="Stocks">
            {/* <div className="Accounts">

                            <select placeholder="Select Account" className="AccountSelector" >
                            {accounts.map((val, i) => <option key={i} value={val}>{val}</option>)}
                            </select>

                        </div> */}
            <div className="MyStocks">
                <div className="StocksGrid" >
                    {filtered.map((stock,i) => (

                        <div key={stock.id} className="StockCard">
                                <h4>{stock.name}</h4>
                                <img className="StockImg" src={require('../Icons/' + stock.stockimg + '.png')}></img>
                                <h4> $ {stock.price}</h4>

                                    <div className="AmountStock">
                                            <h4>Quantity:</h4>
                                            <input placeholder="0"  pattern="/^[1-9\b]+$/" value={stock.quantity} onChange={(e) => handleChange(e, i)} name="quantity"></input> 
                                    </div>

                                    <div className="AmountStock">

                                        <h4> Total Price: $ {(stock.price * stock.quantity).toFixed(2)}</h4>
                                    </div>

                                    <button type="button" onClick={handleBuyStock(stock.name, (stock.price * stock.quantity).toFixed(2))}>Buy</button> 

                        </div>
                    ))}
                </div>
            </div>
        </div>
    )
}


export default SearchResults;
