import React, { useState } from "react";
import logo from '../Icons/logo.png';
import profilepicture from '../Icons/profilepicture.png';
import plus from '../Icons/plus.png';
import erroricon from '../Icons/error.png';
import "../Style/LoginRegister.css";
// import authUser from './AuthDetails';
import { InputMask } from 'primereact/inputmask';
import { Password } from 'primereact/password';
// import ForgotPassword from '../Pages/ForgotPassword';
// import { Button } from 'primereact/button';
// import { Dialog } from 'primereact/dialog';
import { InputText } from 'primereact/inputtext';
import {auth} from "../Database/firebase"; 

import { Link } from "react-router-dom";




// exported out of firebase.js because getAuth is called there
// if it was called here, it was running before InitializeApp method

import { signInWithEmailAndPassword, createUserWithEmailAndPassword, sendEmailVerification, isEmailVerified } from "firebase/auth";

const LoginRegister = () => {

  // hooks for state variables, they can be changed using the method next to it
  const [addclass, setaddclass] = useState("");

  // Hooks to verify the user account

  const [email,setEmail] = useState("");
  const [password,setPassword] = useState("");
  const [ name, setName ] = useState("");

  const [countryCode, setCountryCode] = useState("");
  const [phonenumber, setPhone] = useState("");
  const [identification, setIDFormat] = useState("");
  const [idvalue,setIDValue] = useState(null);
  const [image, setImage] = useState("");
  const [isUploaded, setIsUploaded] = useState(false);
  const [typeFile, setTypeFile] = useState("");




  // method that runs when form is submitted
  // e stands for event
  const logIn = (e) => {
    // when page is reloaded, state of email and password are not reset back to default
    e.preventDefault();
    //returns a promise with firebase answer to our query
    signInWithEmailAndPassword(auth, email, password)
    .then( (userCredential)=>{
        console.log(userCredential)
    }).catch(error => alert(error.message))
  };

  // Sends verification email to the user
  const verifyEmail = () => {
    sendEmailVerification(auth.currentUser)
    .then( () => {
      alert("Email verification sent!");
    })
    .catch( (error) => {
      // When this happen we should put an extra button to send the verification again
      alert("Could not send verification email.");
    });
  };

  // Function to check if password passes all the requirements
  const passValidation = (p) => {
    let regx = /^(?=.*[!@#?])(?=.*[a-z])(?=.*[A-Z])(?=.*[0-9])(?!.*\s).{8,16}$/;
    // contains all required characters
    if ( p.match(regx) ){
      return true;
    }
    // Does not contain all characters
    else{
      alert("Password does not meet the requirements.");
      return false
    }
  }


  // method that runs when form is submitted
  // e stands for event
  const signUp = (e) => {
    // when page is reloaded, state of email and password are not reset back to default
    e.preventDefault();
    // Do all the checks before signing up in the system
    // password check
    if ( !passValidation(password) ){
      return -1
    }

    //returns a promise with firebase answer to our query
    createUserWithEmailAndPassword(auth, email, password)
    .then( (userCredential)=>{
        alert("User created.");
        verifyEmail();

    }).catch(error => alert(error.message));
  };


  const cancel = () => { 
    document.getElementById("signup-form").reset();
    setEmail("")
    setPassword("")
    setIsUploaded(false); 
    setImage(null)
  }

  const clearLogin = () =>{
    cancel();
    setaddclass("");
  }

  const clearSignUp= () =>{
    cancel();
    setaddclass("right-panel-active")
  }



  const phoneMask = (e) => {
    let result;
    if(countryCode === "506"){
      result = "9999-9999"
    }else if(countryCode ==="1"){
      result = "999-999-9999"
    }else{
      result = " "
    }
    return result;
  };



  const idMask = (e) => {
    let result;
    if(identification === "id"){
      result = "99-9999-9999"
    }else if(identification ==="ssn"){
      result = "999-99-9999"
    }else if (identification === "dimex"){
      result = "999999999999"
    }
    else if (identification === "passport"){
      result = "****************************************************************************************************"
    }
    else if (identification === "other"){
      result = "99999999999999999999999"
    }
    return result;
  };


  function handleImageChange(e) {
    if (e.target.files && e.target.files[0]) {
      setTypeFile(e.target.files[0].type);
      let reader = new FileReader();

      reader.onload = function (e) {
        setImage(e.target.result);
        setIsUploaded(true);
      };

      reader.readAsDataURL(e.target.files[0]);
    }
  }




  return (

    <div className="App"> 
      {/* <header className="App-header">
        <nav>
          <img src={logo} className="App-logo" alt="logo" />
          <ul>
            <li><button-header  onClick={() => setaddclass("")}>Sign In</button-header></li>
            <li><button-header onClick={() => setaddclass("right-panel-active")}>Sign Up </button-header> </li>
          </ul>
        </nav>
        </header> */}
    <div className="body">

    <div className={`container ${addclass}`} id="container">
      <div className="form-container  sign-up-container">
        <form onSubmit={signUp} id="signup-form">
          <h1>Create Account</h1>

          <div className="UploadImage">
            <div className="profile-pic">

            {!isUploaded ? (
              <>
                   <label htmlFor="inputImg"> <img src={profilepicture} id="picture" alt="profilepicture" ></img> </label> 
                  <div className="CameraIcon"> <label htmlFor="inputImg"> <img src={plus} id="plus" alt="plus" ></img> </label>     </div>  
                  <input type="file"  accept="image/jpg" id="inputImg" onChange={handleImageChange}/>
              </>
            ): (
              <>
              <label htmlFor="inputImg"> <img id="picture" src={image} draggable={false} alt="uploaded-img" /> </label>
              <div className="CameraIcon"> <img src={erroricon} id="close" alt="plus" onClick={() => {setIsUploaded(false); setImage(null)}} ></img> </div>

              </>
            )}

          </div>
      </div>

          <div className=" NameHolder">

             <div id="Name-Box"> 
                  <input placeholder="Name*" required/>
             </div>

             <div id="MiddleName-Box"> 
             <input type="text" placeholder="Middle Name"/>
             </div>



              <div id="Suffix-Box"> 
              <select id="suffix" name="Suffix" class="form-control">
                    <option>Suffix</option>
                    <option value="Sr." Selected>Sr.</option>
                    <option value="Jr." Selected>Jr.</option>
                    <option value="PhD" Selected>PhD</option>
                    <option value="MSW" Selected>MSW</option>
                    <option value="II" Selected>II</option>
                    <option value="III" Selected>III</option>
                    <option value="IV" Selected>IV</option>
                </select>

             </div> 
          </div>

              <div className="LastName">
                    <input type="text" placeholder="Last Name*" required/>
                    <input type="text" placeholder="Second Last Name" />
              </div>

          <input type="email" placeholder="Email*" required value={email} onChange={(e)=>setEmail(e.target.value)} name="email"/>
          {/* <div className="Password" id="Password">  */}
          <Password placeholder="Password*"  required value={password} onChange={(e)=>setPassword(e.target.value)} name="password" toggleMask />

          {/* </div> */}

          <Password placeholder="Confirm Password*"  required   name="confirmpassword" toggleMask />

          <label-1> Phone Number*</label-1>

         <div className="PhoneNumber">

                <select id="countrycode" name="CountryCode" class="form-control" required onChange={(e) => setCountryCode(e.target.value)}>
                      <option value="0" Selected> Country Code</option>
                            <option data-countryCode="CR" value="506" Selected>CR (+506)</option>
                            <option data-countryCode="US" value="1" Selected>US (+1)</option>
                </select> 
                <InputMask value={phonenumber} onChange={(e) => setPhone(e.target.value)} mask={phoneMask()} placeholder={phoneMask()}> </InputMask>
         </div>

          <div className="Country">
              <label for="country" id="CountrySelector"></label>      
                <select id="country" name="country" class="form-control" required>
                    <option>Country</option>
                    <option value="Costa Rica" Selected>Costa Rica</option>
                    <option value="United States" Selected>United  States</option>
                </select>

              <input type="text" placeholder="Zip Code" name="ZipCode" />
          </div>

          <input type="text" placeholder="Address 1*" required name="Address1"/>
          <input type="text" placeholder="Address 2 " name="Address2"/>

          {/* Buttons: Register & Cancel */}

          <div className="ID">
                <label for="identification" id="IDSelector"></label>      
                  <select id="id" name="id" class="id-control" required onChange={(e) => setIDFormat(e.target.value)}>
                      <option>Identification</option>
                      <option value="id" Selected>ID</option>
                      <option value="ssn" Selected>Social Security Number</option>
                      <option value="dimex" Selected>DIMEX</option>
                      <option value="passport" Selected>Passport</option>
                      <option value="other" Selected>Other</option>
                  </select>
                <InputMask value={idvalue} onChange={(e) => setIDValue(e.target.value)} mask={idMask()} placeholder={idMask()}> </InputMask>
          </div>

          <ul>
            <uli>
                <button type="submit">REGISTER</button>
              <button type="button" onClick={cancel}>CANCEL</button>
            </uli>
          </ul>

        </form>
      </div>
      <div className="form-container sign-in-container">
        <form onSubmit={logIn} id="login-from">
          <h1>Login</h1>
          <input type="email" placeholder="Email" value={email} onChange={(e)=>setEmail(e.target.value)}/>
          <Password placeholder="Password*"  required value={password} onChange={(e)=>setPassword(e.target.value)} name="password" toggleMask />

          <Link to="/forgot"> Forgot Password?</Link>

          <button type="submit">LOGIN</button>
          {/* {err && <span>Something went wrong</span>} */}
        </form>
      </div>
      <div className="overlay-container">
        <div className="overlay">
          <div className="overlay-panel overlay-left">
            <h4> Already have an account? </h4>
            <button
              className="ghost"
              id="signIn"
              onClick={() => clearLogin()}>
              LOG IN
            </button>
          </div>
          <div className="overlay-panel overlay-right">
          <h4> Not a member yet? </h4>
            <button
              className="ghost"
              id="signUp"
              onClick={() => clearSignUp()}
            >
              SIGN UP
            </button>
          </div>
        </div>
      </div>
    </div>
    </div>

    </div>



  );
};

export default LoginRegister;
